mapper.select:
select

from Project


		Name = #{projectName}


		AND Province = #{province}


		AND City = #{city}


		AND id in (select ProjectId from UserProjectRel where UserId = #{userId})



order by


		${sortField}


		Createtime





		${sortDirection}


		desc

select exists (select Name from project


	Name = #{name}

		AND id != #{projectId}


)
